function findUniq(strings) {
    const normalize = str => Array.from(new Set(...(str.toLowerCase().replace(/\s+/g, '').split('')))).sort().join('');

    const counts = {};

    strings.forEach(str => {
        const normalizedStr = normalize(str);
        counts[normalizedStr] = (counts[normalizedStr] || 0) + 1;
    });

    for (let str of strings) {
        if (counts[normalize(str)] === 1) {
            return str;
        }
    }
}```

