```const pagination = (current: number, total: number): (string | number)[] => {
    const delta = 2; // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Å—Ç—Ä–∞–Ω–∏—Ü –¥–æ –∏ –ø–æ—Å–ª–µ —Ç–µ–∫—É—â–µ–π —Å—Ç—Ä–∞–Ω–∏—Ü—ã
    const range = [];

    for (let i = 1; i <= total; i++) {
        if (i === 1 || i === total || i === current || (i >= current - delta && i <= current + delta)) {
            range.push(i);
        }
    }

    // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –≤—ã–≤–æ–¥–∞ —Å —É—á–µ—Ç–æ–º —Ç–µ–∫—É—â–µ–π —Å—Ç—Ä–∞–Ω–∏—Ü—ã –∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–µ –ø—Ä–æ–ø—É—Å–∫–æ–≤
    return range.reduce<(string | number)[]>((acc, num, index, arr) => {
        // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ —Ç–µ–∫—É—â–µ–π —Å—Ç—Ä–∞–Ω–∏—Ü—ã –≤ —Å–ø–µ—Ü–∏–∞–ª—å–Ω–æ–º —Ñ–æ—Ä–º–∞—Ç–µ
        if (num === current) {
            acc.push(`>${num}<`);
        } else {
            acc.push(num);
        }

        // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –ø—Ä–æ–ø—É—Å–∫–æ–≤
        if (index < arr.length - 1) {
            if (arr[index + 1] - num > 1) {
                acc.push('...');
            }
        }

        return acc;
    }, []);
};

```

