```export default function Counter() {
  const [count, setCount] = useState(0);
  let intervalRef = useRef();

  const handleClick = () => {
    setCount(count + 1);
  };

  useEffect(() => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
    intervalRef.current = setInterval(() => setCount(count + 1), 3000);
  }, []);

  return (
    <div>
