const mockServerItems = ["Aquamarine Revenge (Minimal Wear)", "Case Hardened (Field-Tested)", "Jet Set (Well-Worn)", "AUG | Daedalus (Field-Tested)"];
const mockSteamItems = ["Aquamarine Revenge (Minimal Wear)", "Case Hardened (Field-Tested)", "Jet Set (Well-Worn)", "AUG | Daedalus (Field-Tested)", "R8 Revolver | Crazy 8 (Factory New)"];

function saveItemToServerApi(value) {
  return new Promise(resolve => {
    setTimeout(() => resolve(value), 1000)
  })
}

function getServerItemsApi() {
  return new Promise(resolve => {
    setTimeout(() => resolve(mockServerItems), 1000)
  })
}

function getSteamItemsApi() {
  return new Promise(resolve => {
    setTimeout(() => resolve(mockSteamItems), 1000)
  })
}

async function parsingNewItems() {
  try {
    // –ü–æ–ª—É—á–∏—Ç—å —Å–ø–∏—Å–æ–∫ –ø—Ä–µ–¥–º–µ—Ç–æ–≤ –∏–∑ —Å–µ—Ä–≤–µ—Ä–∞
    const serverItems = await getServerItemsApi();
    console.log('Server Items:', serverItems);

    // –ü–æ–ª—É—á–∏—Ç—å —Å–ø–∏—Å–æ–∫ –ø—Ä–µ–¥–º–µ—Ç–æ–≤ –∏–∑ —Å—Ç–∏–º–∞
    const steamItems = await getSteamItemsApi();
    console.log('Steam Items:', steamItems);

    // –û—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞—Ç—å —Å–ø–∏—Å–æ–∫ –ø—Ä–µ–¥–º–µ—Ç–æ–≤ –∏–∑ —Å—Ç–∏–º–∞, –æ—Å—Ç–∞–≤–∏–≤ –ø—Ä–µ–¥–º–µ—Ç—ã, –∫–æ—Ç–æ—Ä—ã—Ö –Ω–µ—Ç –Ω–∞ —Å–µ—Ä–≤–µ—Ä–µ
    const newItems = steamItems.filter(item => !serverItems.includes(item));
    console.log('New Items:', newItems);

    // –î–æ–±–∞–≤–ª—è—Ç—å –Ω–æ–≤—ã–µ –ø—Ä–µ–¥–º–µ—Ç—ã –Ω–∞ —Å–µ—Ä–≤–µ—Ä, –æ–¥–Ω–æ–≤—Ä–µ–º–µ–Ω–Ω–æ –Ω–µ –±–æ–ª–µ–µ –¥–≤—É—Ö –ø—Ä–µ–¥–º–µ—Ç–æ–≤
    for (let i = 0; i < newItems.length; i += 2) {
      const itemsToSave = newItems.slice(i, i + 2);
      console.log('Saving Items:', itemsToSave);
      await Promise.all(itemsToSave.map(item => saveItemToServerApi(item)));
      console.log('Saved Items:', itemsToSave);
    }

  } catch (error) {
    console.error('Error:', error);
  }
}```

```// —Ç–∏–ø–∏–∑–∏—Ä–æ–≤–∞—Ç—å —Ñ—É–Ω–∫—Ü–∏—é getDetails

const mappingValues = {
   name: (value: string) => Name: ${value},
}

type User = {
   name: string;
   age: number;
}

const user: User = {
  name: 'Oleg',
  age: 22,
}

function getDetails(obj, mapping) {
   const result: Array<{ key: string; value: unknown }> = []
   for (const key in obj) {
      const value = obj[key]
      const fn = mapping[key]
      result.push({ key, value: fn ? fn(value) : value })
   }
   return result
}

const details = getDetails(user, mappingValues)

//—Ä–µ—à–µ–Ω–∏–µ
type Mapping<T> = {
  [K in keyof T]?: (value: T[K]) => unknown;
};

function getDetails<T extends object>(obj: T, mapping: Mapping<T>)```

