var readonlyPart = (() => {
  const tasks = ['task1', 'task2', 'task3'];
  const rawTasksDelay = Math.random() * 2000;
  const unclaimDelay = 1000 + Math.random() * 1000;

  const render = (tasks) => {
    document.querySelector('.tasks').innerHTML = `${tasks.map( item => `<div> ${item}  <button data-id="${item}" onClick="readonlyPart.unclaim('${item}')">unclaim</button> </div>` ).join('') }`;
  };

  const getRawTasks = () => {
    return new Promise(resolve => {
      setTimeout(() => {
        resolve([...tasks]);
      }, rawTasksDelay);
    }); 
  };

  const asyncUnclaim = (task) => {
    return new Promise((resolve) => {
      setTimeout(() => {
        _.remove(tasks, _ => _ === task);
        resolve();
      }, unclaimDelay);
    });
  };

  const unclaim = (task) => {
    requestUnclaim(task);
    refreshTasks();
  };

  const refreshTasks = () => {
    const el = document.querySelector(".refresh")
    el.disabled = true; 
    return getTasks().then(render).then( () => el.disabled = false);
  };

  document.querySelector(".refresh").onclick = refreshTasks;

  return { refreshTasks, getRawTasks, asyncUnclaim, unclaim }
})();

readonlyPart.refreshTasks();

//----


//read-write
function requestUnclaim(task) {
// –†–ï–®–ï–ù–ò–ï
  readonlyPart.asyncUnclaim(task).then(() => readonlyPart.refreshTasks())
}

function getTasks() {
  return readonlyPart.getRawTasks();
}
```

