  }
  for (var i = 0; i < array.length; i++) {
    newArray.push(array[i]); // –î–æ–±–∞–≤–ª—è–µ–º –¥—É–±–ª–∏–∫–∞—Ç—ã —ç–ª–µ–º–µ–Ω—Ç–æ–≤
  }
  return newArray; // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –Ω–æ–≤—ã–π –º–∞—Å—Å–∏–≤
}

