function fetchApi (apis) {
        const fetchPromises = apis.map((url, index) => {
            fetch(url).then(data => ({priority: index, data})).catch(error => {(priority: index, error)} )
        })

    return Promise.allSettled(
        fetchPromises
        ).then(results => {
            const successful = results.filter(result => result.status === 'fulfilled' && !result.value.error) 

            if(successful.length === 0){
                throw new Error('All requests failed')
            }

        successful.sort((a, b) => a.value.priority - b.value.priority)

        return successful[0].value.data

    })


}





