function canGetCount(n) {
  let count = 0;

  return function () {
    count++;
    if (count <= n) {
      return "yes";
    } else {
      return "no";
    }
  };
}

const getOne = canGetCount(2);

console.log(getOne(), 1); // yes 1
console.log(getOne(), 2); // yes 2
console.log(getOne(), 3); // no 3
console.log(getOne(), 4); // no 4```

