function solution(n: number, isBadVersion: (idx: number) => boolean): number {
    let pointer1 = 0
    let pointer2 = n - 1
    let averagePointer = -1
        while (pointer1 <= pointer2) {
        let middle = Number.floor((pointer2 + pointer1) / 2)

        if (isBadVersion(middle)) {
            // –≤ –ª–µ–≤–æ–π –ø–æ–ª–æ–≤–∏–Ω–µ –∏—Å–∫–∞—Ç—å
            pointer2 = middle
            averagePointer = middle
        } else {
            pointer1 = middle
        }    
    }
  return averagePointer
}



const testArr = [true, true, true, false, false];
const expect = 3;
const ibv = createIsBadVersion(testArr);


const answer = solution(testArr.length, ibv);
console.assert(answer === expect, `Bad answer ${answer}`);

const testArr2 = new Array(9999999).fill(true);
testArr2.push(false)
const expect2 = 9999999;
const ibv2 = createIsBadVersion(testArr2);


const answer2 = solution(testArr2.length, ibv2);
console.assert(answer2 === expect2, `Bad answer ${answer2}`);

function createIsBadVersion(arr: boolean[]) {
  return function isBadVersion(idx: number) {
    return !arr[idx];
  }
}
