function getTreeValues(tree) {
    const result = {};
    const stack = [{ node: tree, prefix: '' }]

    while (stack.length) {
        const { node, prefix } = stack.pop();

        Object.keys(node).forEach((key) => {
            const value = node[key];
            const currentPath = prefix ? `${prefix}${key}` : key

            if (typeof value === 'object') {
