function parallel(funcArray, doneAll) {
  const result = [];
  let countFn = 0;
  funcArray.forEach((fn, index) => {
    fn((res) => {
      result[index] = res;
      countFn += 1;
      if (countFn == funcArray.length) {
        doneAll(result);
      }
    });
  });
}```

