```function debounce(fn, time) {
    let timeoutId;

    return function(...args) {
        if (timeoutId) {
            clearTimeout(timeoutId);
        }
      timeoutId = setTimeout(() => {
            fn.apply(this, args);
        }, time);
    }
}```

