 * –ù—É–∂–Ω–æ –Ω–∞–ø–∏—Å–∞—Ç—å —Ñ—É–Ω–∫—Ü–∏—é get. –ù–∞ –≤—Ö–æ–¥ —Ñ—É–Ω–∫—Ü–∏—è –ø—Ä–∏–Ω–∏–º–∞–µ—Ç –æ–±—ä–µ–∫—Ç –∏ –ø—É—Ç—å –¥–æ –ø–æ–ª—è –æ–±—ä–µ–∫—Ç–∞. 
 * –ü—É—Ç—å ‚Äì —ç—Ç–æ —Å—Ç—Ä–æ–∫–∞, —Ä–∞–∑–¥–µ–ª–µ–Ω–Ω–∞—è —Ç–æ—á–∫–æ–π. –§—É–Ω–∫—Ü–∏—è –¥–æ–ª–∂–Ω–∞ –≤–µ—Ä–Ω—É—Ç—å —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–µ–µ –ø–æ–ª–µ –æ–±—ä–µ–∫—Ç–∞. 
 * –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ–º–æ–≥–æ –ø–æ–ª—è –≤ –æ–±—ä–µ–∫—Ç–µ –º–æ–∂–µ—Ç –Ω–µ –±—ã—Ç—å.
 */

function get(obj, path) {
    if (!obj) return undefined;
    const keys = path.split(".")
    let result = obj;

    for(let i = 0; i < keys.length; i++){
        const currKey = keys[i];
        const curr = result?.[currKey];

        if(curr === undefined) return undefined;
        if(curr === null && i === keys.length - 1) return null;

        result = result[currKey]
    }

    return result;
}

