const isMono = (arr) => {
    let isGoingUp;

    for(let i = 0; i < arr.length - 1; i++){
        const cur = arr[i]
        const next = arr[i + 1];

        if(curr === next) continue;

       if(cur < next && isGoingUp === false) {
            return false;
        } else if(cur > next && isGoingUp) {
            return false;
        }
    }

    return true;
}


